-- ===== DDL (Compact): Region → Subregion → District → Admin_Unit =====
CREATE TABLE IF NOT EXISTS region (
  region_id SERIAL PRIMARY KEY,
  name TEXT UNIQUE NOT NULL
);

CREATE TABLE IF NOT EXISTS subregion (
  subregion_id SERIAL PRIMARY KEY,
  name TEXT NOT NULL,
  region_id INT NOT NULL REFERENCES region(region_id) ON DELETE CASCADE,
  UNIQUE(name, region_id)
);

CREATE TABLE IF NOT EXISTS district (
  district_id SERIAL PRIMARY KEY,
  name TEXT NOT NULL,
  subregion_id INT NOT NULL REFERENCES subregion(subregion_id) ON DELETE CASCADE,
  UNIQUE(name, subregion_id)
);

-- Admin units at LC3 level: could be Subcounty, Town Council, or City Division.
CREATE TABLE IF NOT EXISTS admin_unit_lc3 (
  admin_unit_id SERIAL PRIMARY KEY,
  name TEXT NOT NULL,
  unit_type TEXT DEFAULT 'LC3', -- unknown granular type in this dataset
  district_id INT NOT NULL REFERENCES district(district_id) ON DELETE CASCADE,
  UNIQUE(name, district_id)
);


